// Copyright (c) Microsoft Corporation. All rights reserved.

using System.Collections.Concurrent;
using Conversations.Service.Contracts;
using Conversations.Service.Contracts.SemanticSearch;
using Microsoft.ServiceHub.Framework;
using Microsoft.VisualStudio.Conversations.Internal.Telemetry;
using Microsoft.VisualStudio.Conversations.Service.Contracts;
using Microsoft.VisualStudio.Copilot.Core.Repository.AzureDevOps;
using Microsoft.VisualStudio.Copilot.Tracing;
using Microsoft.VisualStudio.Copilot.UnstableInternalApi.Indexing;
using Microsoft.VisualStudio.Telemetry;
using Microsoft.VisualStudio.Threading;
using static Microsoft.VisualStudio.Conversations.Internal.Telemetry.TelemetryConstants.EventNames;

namespace Microsoft.VisualStudio.Copilot.Core.Repository.RemoteSemanticSearch
{
    public class AzureDevOpsSemanticSearchStrategy : SearchStrategyBase
    {
        public AzureDevOpsSemanticSearchStrategy(
        ConcurrentDictionary<string, RemoteRepositoryInfo> repoInfoCache,
        Lazy<IRepositoryManagerFactory> repositoryManagerFactory,
        IServiceBroker serviceBroker,
        ICopilotExtensionTraceSource copilotExtensionTraceSource)
        : base(repoInfoCache, repositoryManagerFactory, serviceBroker, copilotExtensionTraceSource, RemoteKind.ADO)
        {
        }

        /// <inheritdoc/>
        public async override Task<RemoteRepositoryInfo> GetRemoteRepositoryInfoAsync(string workspacePath, CopilotCorrelationId correlationId, string sourceRequest, GitRepositoryExclusionRuleDomain? localRepoInfo, CancellationToken cancellationToken)
        {
            var telemetryScope = TelemetryService.DefaultSession.StartOperation(TelemetryConstants.EventNames.RemoteSemanticSearch.GetAzureDevOpsRepoStatus, new Dictionary<string, object>
            {
                [TelemetryConstants.PropertyNames.CorrelationId] = correlationId,
                [TelemetryConstants.PropertyNames.Source] = sourceRequest,
            }, ConversationsTelemetry.ContextBag);

            Assumes.Present(workspacePath);
            var normalizedPath = NormalizeAndTrimSolutionPath(workspacePath);
            var nonRemoteRepositoryInfo = RemoteRepositoryInfo.CreateNonExistentRepoInfo(normalizedPath);

            try
            {
                //We try to retrieve the repository info from repoInfoCache first to avoid checking for repostory info again.
                if (!this.repoInfoCache.TryGetValue(normalizedPath, out var cacheEntry))
                {
                    this.trace.Verbose($"Attempted to get AzureDevOps repository status");

                    localRepoInfo = localRepoInfo is not null ? localRepoInfo : await this.repositoryManagerLazy.Value.GetRemoteRepoInfoAsync(normalizedPath, cancellationToken);

                    if (localRepoInfo is null)
                    {
                        this.repoInfoCache[normalizedPath] = nonRemoteRepositoryInfo;
                        this.trace.Error($"Repository is not in AzureDevOps, CorrelationId: ${correlationId}");
                        telemetryScope.EndEvent.Properties[TelemetryConstants.PropertyNames.RemoteSemanticSearch.RemoteRepoStatus] = "NotRemoteRepo";
                        telemetryScope.End(TelemetryResult.Success, "Not a remote repository");

                        return nonRemoteRepositoryInfo;
                    }

                    if (string.IsNullOrWhiteSpace(localRepoInfo.baseFolder.Path))
                    {
                        this.trace.Error($"Exception triggered attempting to get remote repo info. Repository base folder is null or empty, CorrelationId: ${correlationId}");
                        telemetryScope.EndEvent.Properties[TelemetryConstants.PropertyNames.RemoteSemanticSearch.RemoteRepoStatus] = "BaseFolderNotSet";
                        telemetryScope.End(TelemetryResult.Failure, "Repository base folder is not set");
                        return nonRemoteRepositoryInfo;
                    }

                    this.repoBaseFolder = this.GetNormalizedBaseFolderWithCorrectCasing(localRepoInfo.baseFolder.Path, normalizedPath);

                    cacheEntry = new RemoteRepositoryInfo(
                        normalizedPath,
                        localRepoInfo.Owner!,
                        localRepoInfo.AdoOrganization!,
                        localRepoInfo.Name!,
                        this.RemoteKind,
                        // remoteIndexStatus placeholder:
                        nonRemoteRepositoryInfo.RemoteIndexStatus);

                    var isFeatureFlagEnabled = await GetOptionsAsync(
                            this.serviceBroker,
                            options => options.EnableAzureDevOpsSemanticSearchAsync(cancellationToken),
                            cancellationToken);

                    if (!isFeatureFlagEnabled)
                    {
                        telemetryScope.EndEvent.Properties[TelemetryConstants.PropertyNames.RemoteSemanticSearch.RemoteRepoStatus] = "FeatureFlagDisabled";
                        telemetryScope.End(TelemetryResult.Success, "AzureDevOps Semantic Search feature flag is disabled.");
                        this.repoInfoCache[normalizedPath] = cacheEntry;

                        return cacheEntry;
                    }
                }
                else
                {
                    // In this case, we don't check if the repo cannot execute semantic search like we do in GitHub strategy,
                    // because Azure DevOps repositories should be always indexed in theory, for GitHub we have the capability to
                    // trigger an indexing operation, but for Azure DevOps we don't. We return the cached entry.
                    telemetryScope.End(TelemetryResult.Success);
                    return cacheEntry;
                }

                var repoOwner = cacheEntry.RepoOwner;
                var repoName = cacheEntry.RepoName;
                var repoOrg = cacheEntry.RepoOrganization;

                //Gets the latest status of Remote Azure DevOps repository.
                var repoStatus = await this.GetAzureDevOpsRepoIndexStatusAsync(repoOrg!, repoOwner, repoName, telemetryScope, cancellationToken);
                var semanticSearchEnabled = repoStatus.SemanticSearchEnabled;
                this.trace.Verbose($"{repoOrg}/{repoOwner}/{repoName} repository status: SemanticCodeSearch({semanticSearchEnabled})");

                cacheEntry = new RemoteRepositoryInfo(
                    normalizedPath,
                    repoOwner!,
                    repoOrg,
                    repoName!,
                    this.RemoteKind,
                    repoStatus);

                this.repoInfoCache[normalizedPath] = cacheEntry;
                telemetryScope.End(TelemetryResult.Success);

                return cacheEntry;
            }
            catch (OperationCanceledException)
            {
                telemetryScope.End(TelemetryResult.Failure, "Operation was canceled.");
                throw;
            }
            catch (Exception ex)
            {
                // We can’t use a custom or specific exception here because it’s triggered out-of-proc.
                // When an exception crosses process boundaries, the specific exception type is not preserved.
                // An example of this specific exception ocurrs when the user needs to re-enter VS credentials.
                // In this case, we temporarily return a non remote repository and check again later.
                if (ex.Message.Contains("Acquired token is null", StringComparison.OrdinalIgnoreCase))
                {
                    telemetryScope.End(TelemetryResult.Failure, $"GetRemoteRepositoryInfo failed to retrieve access token.");
                    this.trace.Error($"Exception triggered attempting to retrieve access token, CorrelationId: {correlationId}");
                    return nonRemoteRepositoryInfo;
                }

                telemetryScope.End(TelemetryResult.Failure, $"GetRemoteRepositoryInfo operation failed.");
                this.trace.Error($"Exception triggered attempting to get remote repo info, CorrelationId: {correlationId}");
                TelemetryService.DefaultSession.PostFault(Indexing.SemanticSearchFault, ex, telemetryScope.Correlation, ex.ToDetailsPropertyBag().WithContext());

                throw;
            }
        }

        /// <inheritdoc/>
        public override async Task<IReadOnlyList<RerankedSearchResult>> SearchAsync(
            string[] searchQueries,
            string userPrompt,
            CopilotCorrelationId correlationId,
            string workspacePath,
            string sourceRequest,
            int maxResults,
            CancellationToken cancellationToken)
        {
            var telemetryScope = TelemetryService.DefaultSession.StartOperation(TelemetryConstants.EventNames.RemoteSemanticSearch.RemoteSemanticSearchResults, new Dictionary<string, object>
            {
                [TelemetryConstants.PropertyNames.CorrelationId] = correlationId,
                [TelemetryConstants.PropertyNames.Source] = sourceRequest,
                [TelemetryConstants.PropertyNames.RemoteSemanticSearch.RemoteRepoKindStatus] = this.RemoteKind,
            }, ConversationsTelemetry.ContextBag);

            try
            {
                if (searchQueries is null || searchQueries.Length == 0)
                {
                    return [];
                }

                this.trace.Info($"Attempted to get semantic search results. Search terms: {string.Join(",", searchQueries)}");
                var normalizedPath = NormalizeAndTrimSolutionPath(workspacePath);

                if (!this.repoInfoCache.TryGetValue(normalizedPath, out var cacheEntry))
                {
                    cacheEntry = await this.GetRemoteRepositoryInfoAsync(normalizedPath, correlationId, sourceRequest, localRepoInfo: null, cancellationToken);
                }

                using var adoService = await this.serviceBroker.GetProxyAsync<IAzureDevOpsService>(ServiceIdentity.VSConversationsServiceDescriptor, cancellationToken);
                Assumes.Present(adoService);

                var searchTasks = searchQueries.Select(async searchQuery =>
                {
                    this.trace.Info($"Executing semantic search for request {correlationId}, prompt: {searchQuery}");

                    var adoRequest = new AzureDevOpsSemanticSearchRequest()
                    {
                        Organization = cacheEntry.RepoOrganization,
                        Project = cacheEntry.RepoOwner,
                        RepositoryName = cacheEntry.RepoName,
                        Prompt = searchQuery,
                        Limit = maxResults,
                    };

                    var response = await adoService.SemanticSearchAsync(adoRequest, cancellationToken);
                    var results = response?.Results ?? Array.Empty<RerankedSearchResult>();

                    if (results.Length > 0)
                    {
                        var mostRankedResultDistance = results.First().Distance;
                        var avgScore = results.Average(r => r.Distance);
                        var resultsCount = results.Length;

                        telemetryScope.EndEvent.Properties[TelemetryConstants.PropertyNames.RemoteSemanticSearch.RemoteSemanticSearchResultCount] = resultsCount;
                        telemetryScope.EndEvent.Properties[TelemetryConstants.PropertyNames.RemoteSemanticSearch.RemoteSemanticSearchBestResultDistance] = mostRankedResultDistance;
                        telemetryScope.EndEvent.Properties[TelemetryConstants.PropertyNames.RemoteSemanticSearch.RemoteSemanticSearchResultsAvg] = avgScore;

                        this.trace.Info($"Total of results retrieved: {resultsCount}");
                    }
                    else
                    {
                        this.trace.Info("No results retrieved");
                    }

                    return results;
                }).ToArray();

                var results = await Task.WhenAll(searchTasks);

                // Flatten results from all tasks
                var flattenedResults = results.SelectMany(r => r).ToArray();

                telemetryScope.End(TelemetryResult.Success);
                return flattenedResults;
            }
            catch (OperationCanceledException)
            {
                telemetryScope.End(TelemetryResult.Failure, "Operation was canceled.");
                throw;
            }
            catch (Exception ex)
            {
                telemetryScope.End(TelemetryResult.Failure, $"AzureDevOps remote semantic search failed");
                this.trace.Error($"Exception getting AzureDevOps semantic search results. CorrelationId: '{correlationId}'");
                TelemetryService.DefaultSession.PostFault(Indexing.SemanticSearchFault, ex, telemetryScope.Correlation, ex.ToDetailsPropertyBag().WithContext());
                throw;
            }
        }

        /// <inheritdoc/>
        public override async Task<bool> CanExecuteSemanticSearchAsync(
            string workspacePath,
            CopilotCorrelationId correlationId,
            string sourceRequest,
            bool triggerIndexIfNotIndexed,
            CancellationToken cancellationToken)
        {
            if (workspacePath is null)
            {
                return false;
            }

            var normalizedPath = NormalizeAndTrimSolutionPath(workspacePath);

            this.trace.Verbose($"SemanticSearchIndexChecker: retrieving info for repo at {normalizedPath}");

            if (!this.repoInfoCache.TryGetValue(normalizedPath, out var cacheEntry))
            {
                cacheEntry = await this.GetRemoteRepositoryInfoAsync(normalizedPath, correlationId, sourceRequest, localRepoInfo: null, cancellationToken);
            }

            if (cacheEntry == null)
            {
                return false;
            }

            var isFeatureFlagEnabled = await GetOptionsAsync(
                        this.serviceBroker,
                        options => options.EnableAzureDevOpsSemanticSearchAsync(cancellationToken),
                        cancellationToken);

            return isFeatureFlagEnabled && cacheEntry.CanExecuteSemanticSearch(isFeatureFlagEnabled);
        }

        /// <inheritdoc/>
        public override async Task<bool> InitializeRepoIndexAsync(string workspacePath, CopilotCorrelationId correlationId, string sourceRequest, CancellationToken cancellationToken)
        {
            // We are assuming that all ADO repositories are already indexed, so we just mark it as initialized.
            var normalizedPath = NormalizeAndTrimSolutionPath(workspacePath);

            if (!this.repoInfoCache.TryGetValue(normalizedPath, out var cacheEntry))
            {
                cacheEntry = await this.GetRemoteRepositoryInfoAsync(normalizedPath, correlationId, sourceRequest, localRepoInfo: null, cancellationToken);
            }

            if (cacheEntry.IsInitialized)
            {
                return false;
            }

            var isFeatureFlagEnabled = await GetOptionsAsync(
                        this.serviceBroker,
                        options => options.EnableAzureDevOpsSemanticSearchAsync(cancellationToken),
                        cancellationToken);

            if (!isFeatureFlagEnabled)
            {
                return false;
            }

            //Thread safe
            this.repoInfoCache[normalizedPath] = cacheEntry.Initialize();

            return true;
        }

        private async Task<AzureDevOpsRemoteIndexStatus> GetAzureDevOpsRepoIndexStatusAsync(
            string repoOrganization,
            string repoOwner,
            string repoName,
            TelemetryScope<OperationEvent> telemetryScope,
            CancellationToken cancellationToken)
        {
            using var capi = await this.serviceBroker.GetProxyAsync<IAzureDevOpsService>(ServiceIdentity.VSConversationsServiceDescriptor, cancellationToken);
            Assumes.Present(capi);

            var remoteIndexRequest = new AzureDevOpsSemanticSearchRequest
            {
                Organization = repoOrganization,
                Project = repoOwner,
                RepositoryName = repoName,
            };

            var repoStatus = await capi.GetAzureDevOpsRepoIndexStateAsync(remoteIndexRequest, cancellationToken);

            telemetryScope.EndEvent.Properties[TelemetryConstants.PropertyNames.RemoteSemanticSearch.RemoteRepoStatus] = repoStatus.SemanticSearchEnabled ? "Indexed" : "NotIndexed";

            return repoStatus;
        }
    }
}

