/// <summary>
/// Defines the contract for remote semantic search strategies, providing methods for executing semantic search operations
/// and managing remote repository information and indexing.
/// </summary>
public interface ISearchStrategy { 
    /// <summary>
    /// Executes a search against a remote repository or chunk index db depending on the case
    /// using the specified prompt and workspace context.
    /// </summary>
    /// <param name="searchQueries">The search queries to be used.</param>
    /// <param name="userPrompt">The original user prompt. This is used by lexical strategy</param>
    /// <param name="correlationId">The correlation ID for telemetry and tracking.</param>
    /// <param name="workspacePath">The workspace or solution path.</param>
    /// <param name="sourceRequest">The source of the request for telemetry.</param>
    /// <param name="maxResults">Maximum number of chunk results.</param>
    /// <param name="cancellationToken">A token to monitor for cancellation requests.</param>
    /// <returns>A list of semantic search results.</returns>
    Task<IReadOnlyList<RerankedSearchResult>> SearchAsync(
    string[] searchQueries,
    string userPrompt,
    CopilotCorrelationId correlationId,
    string workspacePath,
    string sourceRequest,
    int maxResults,
    CancellationToken cancellationToken);
    /// <summary>
    /// Determines whether semantic search can be executed for the specified workspace, optionally triggering indexing if not already indexed.
    /// </summary>
    /// <param name="workspacePath">The workspace or solution path.</param>
    /// <param name="correlationId">The correlation ID for telemetry and tracking.</param>
    /// <param name="sourceRequest">The source of the request for telemetry.</param>
    /// <param name="triggerIndexIfNotIndexed">Whether to trigger indexing if not already indexed.</param>
    /// <param name="cancellationToken">A token to monitor for cancellation requests.</param>
    /// <returns>True if semantic search can be executed; otherwise, false.</returns>
    Task<bool> CanExecuteSemanticSearchAsync(
    string workspacePath,
    CopilotCorrelationId correlationId,
    string sourceRequest,
    bool triggerIndexIfNotIndexed,
    CancellationToken cancellationToken);
    /// <summary>
    /// Retrieves remote repository information for the specified workspace path.
    /// </summary>
    /// <param name="workspacePath">The workspace or solution path.</param>
    /// <param name="correlationId">The correlation ID for telemetry and tracking.</param>
    /// <param name="sourceRequest">The source of the request for telemetry.</param>
    /// <param name="localRepoInfo">Local repo info used to get repo owner, name and organization, it is passed along if present to avoid retrieving again locally</param>
    /// <param name="cancellationToken">A token to monitor for cancellation requests.</param>
